// Minimal fallback for bootstrap JS (for when CDN is blocked).
window.bootstrap = window.bootstrap || {};
// Minimal Alert dismissal support used in templates (btn-close)
(function(){
  document.addEventListener('click', function(e){
     var t = e.target;
     if (t && t.matches && t.matches('[data-bs-dismiss="alert"]')) {
         var a = t.closest('.alert');
         if (a) a.style.display = 'none';
     }
  });
})();
